mapper.delete:
delete
from role_menu
where role_id = #{roleId,jdbcType=BIGINT}
  and menu_id = #{menuId,jdbcType=BIGINT}

mapper.update:
update role_menu
set create_by   = #{createBy,jdbcType=VARCHAR},
    create_time = #{createTime,jdbcType=TIMESTAMP},
    update_by   = #{updateBy,jdbcType=VARCHAR},
    update_time = #{updateTime,jdbcType=TIMESTAMP}
where role_id = #{roleId,jdbcType=BIGINT}
  and menu_id = #{menuId,jdbcType=BIGINT}

mapper.select:
select role_id, menu_id, create_by, create_time, update_by, update_time
from role_menu
where role_id = #{roleId,jdbcType=BIGINT}
  and menu_id = #{menuId,jdbcType=BIGINT}

select role_id, menu_id, create_by, create_time, update_by, update_time
from role_menu
where role_id = #{roleId,jdbcType=BIGINT}

mapper.update:
update role_menu
set create_by = #{newEditor,jdbcType=VARCHAR}
where create_by = #{newEditor,jdbcType=VARCHAR};
update role_menu
set update_by = #{newEditor,jdbcType=VARCHAR}
where update_by = #{newEditor,jdbcType=VARCHAR};
